package com.cherrypicks.tcc.util;

import java.io.BufferedOutputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.UUID;

import org.apache.commons.codec.binary.Base64;
import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;

public class DecodeImageUtil {

	private static final Log LOG = LogFactory.getLog(DecodeImageUtil.class);

	/**
	 * 将一个base64转换成图片保存在 path 文件夹下 名为imgName.jpg
	 * @param base64String
	 * data:image/jpeg;base64,
	 * @param uploadPath  是一个文件夹路径
	 * @param imgName 图片名字(有后缀)
	 * @throws IOException
	 * @throws Exception
	 */
	public static String saveBase64PictoServer(final String base64ImageString,String uploadPath) throws IOException{

	    final int indexImage = base64ImageString.indexOf("base64");
	    final String realImage = base64ImageString.substring(indexImage + 7, base64ImageString.length());
	    final int indexSlash = base64ImageString.indexOf("/");
	    final int indexColon = base64ImageString.indexOf(";");
	    final String suffix = base64ImageString.substring(indexSlash + 1, indexColon);

		if (uploadPath.lastIndexOf("/") != uploadPath.length() - 1) {
            uploadPath += "/";
        }

		final File uploadFolder = new File(uploadPath);
		if (!uploadFolder.exists()) {
			uploadFolder.mkdirs();
		}
		final String imageName =  UUID.randomUUID().toString().replace("-", "") + "." + suffix;

		BufferedOutputStream bw = null;
		try {
			bw = new BufferedOutputStream(new FileOutputStream(uploadPath + imageName));
			final byte[] bytes = Base64.decodeBase64(realImage);
			bw.write(bytes);
		} finally {
			try {
				if (bw != null) {
					bw.flush();
				}
				if (bw != null) {
					bw.close();
				}
			} catch(final IOException err) {
				LOG.error(" IO close Exception",err);
			}
		}
		return imageName;
	}


	/**
	 * 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
	 * @param imgFile
	 * @return Base64编码过的字节数组字符串
	 * @throws IOException
	 */
	public static String getBase64ImageStr(final String imgFile) throws IOException {
		InputStream in = null;
		byte[] data = null;
		try {
			in = new FileInputStream(imgFile);
			data = new byte[in.available()];
			in.read(data);
		} finally {
			try {
				if (in != null) {
					in.close();
				}
			} catch(final IOException err) {
				LOG.error(" IO close Exception",err);
			}
		}
		return Base64.encodeBase64String(data);
	}

	public static void main(final String[] args) throws IOException {
	    final String base64String = "data:image/jpeg;base64,";
	    System.out.println(DecodeImageUtil.saveBase64PictoServer(base64String, "/data/upload/shoppo/1"));
	    System.out.println(File.separator);
    }

}
